import React, { Component, lazy, Suspense } from 'react';
// import React, {Component} from "react";
import logo from './logo.svg';

//Data
import PhotoData from "./testData";

//Lasy
const LazyPhotoComponet = lazy(() => import("./lazyComponet"));
// const LasyAlbumComponet = lazy(() => import("./lazyComponet"));

// import LazyPhotoComponet from "./lazyComponet";



class lazyContainer extends Component {
    constructor(){
        super();
        this.state = {
            albumDB: [],
            photoDB: [],
            isConected: false,
            albumLevel: false,
            albumIndex: 0,
            photoIndex: -1,
            isMore: true
        };
    };

    componentDidMount(){
        // fetch('https://jsonplaceholder.typicode.com/photos/1')
        fetch('https://jsonplaceholder.typicode.com/photos/')
        .then(response => response.json())
        .then(data => {

            let count = Object.keys(data).length;
            let db=[], x = 0, num = 0;

            for(let i=0; i<count; i++){
                // console.log("== " + data[i].albumId + " " + data[i].id)
                if(data[i].albumId !== num){
                    num = data[i].albumId;
                    db[x] = {"albumId": num, "id": data[i].id, "thumbnailUrl": data[i].thumbnailUrl, "title": data[i].title};
                    // console.log("+= " + num + " - " + x)
                    x++;
                    // console.log(db);
                }
            }



            this.setState({
                albumDB: db,
                photoDB: db,
                isConected: true,
                albumLevel: true
            });
            // console.log(data);
            console.log("What 1")
        });
    };

    handleClick = (event) => {/*No bind needed */
        console.log("e " + event.target.id);
        let id = event.target.id, album = 0, urlIndex = 0;
        // if(id === "photo_"){
        //     this.setState({
        //         photoIndex: -1
        //     })
        // } else {
        //     this.setState({
        //         photoIndex: id
        //     })
        // }

        if(this.state.albumLevel){//Get the clicked album id
            let x = 0, y = parseInt(id);
            for (x in this.state.photoDB){
                // console.log ("Album is == "+ this.state.photoDB[x].albumId + " " + this.state.photoDB[x].id)
                if(this.state.photoDB[x].id === y){
                    album = this.state.photoDB[x].albumId;
                    console.log ("Album is == "+ this.state.photoDB[x].albumId + " " + this.state.photoDB[x].id)
                }
                // if(this.state.photoDB[x].id === parseInt(id)){
                //     urlIndex = x;
                //     console.log ("urlIndex is == "+ x + " " + this.state.photoDB[x].id)
                // }

            }

        let s = parseInt(id) - 1, end = s+5; //s = 0; end = 5;
        fetch('https://jsonplaceholder.typicode.com/photos?_start='+ s + '&_end=' + end)
        .then(response => response.json())
        .then(data => {
            this.setState({
                albumIndex: album,
                albumLevel: false,
                photoIndex: -1,
                photoDB: data,
                isConected: true,
            });
            // console.log(data);
            console.log("What x")
        });
            // this.setState({
            //     albumIndex: id,
            //     albumLevel: false,
            //     photoIndex: -1,
            //     photoDB: PhotoData
            // })
        }
        else {//Inside selected Album
            if(id === "backToAlbum"){//If back to Album is clicked
                this.setState({
                    photoDB: this.state.albumDB,
                    albumIndex: 0,
                    albumLevel: true,
                    photoIndex: -1
                })
            }
            else if(id === "loadMore"){
                let count = Object.keys(this.state.photoDB).length,
                s = parseInt(this.state.photoDB[count-1].id), end = s+5;;
                console.log(" x " + this.state.photoDB[count-1].id)

                fetch('https://jsonplaceholder.typicode.com/photos?_start='+ s + '&_end=' + end)
                .then(response => response.json())
                .then(data => {
                    let db = this.state.photoDB.concat(data)//Object.assign(this.state.photoDB, data);
                    console.log(data);
                    console.log(db);

                    this.setState({
                        photoDB: db
                    });
                    // console.log(data);
                    console.log("What x")
                });
            }
            else {//If Photo is clicked
                if(this.state.albumIndex) {//If Album id is not 0
                    if(id === "photo_"){//If selected Photo is in full view then close it
                        this.setState({
                            photoIndex: -1
                        })
                    } else {//Display the selected photo in full view
                        let x = 0;
                        for (x in this.state.photoDB){
                            if(this.state.photoDB[x].id === parseInt(id)){
                                urlIndex = x;
                                console.log ("urlIndex is == "+ x + " " + this.state.photoDB[x].id)
                            }
            
                        }
                        this.setState({
                            photoIndex: urlIndex
                        })
                    }
                }
            }

        }

        // console.log(" ** * " + this.state.albumIndex + " " + this.state.photoIndex)
    }

    render() {
        // const photoData = this.state.photoDB;
        // console.log("pDATA " + photoData[0])
        // var myJSON = JSON.stringify(photoData[0]);
        // console.log("pDATA " + myJSON)
        // console.log(" ")
        // console.log("PDATA " + PhotoData)
        // myJSON = JSON.stringify(PhotoData);
        // console.log("pDATA " + myJSON)
        // var count = Object.keys(PhotoData).length;
        // console.log(count);

        // let x = 0, num = 0;        
        // let db=[];

        // for(let i=0; i<count; i++){
        //     console.log("== " + PhotoData[i].albumId + " " + PhotoData[i].id)
        //     if(PhotoData[i].albumId !== num){
        //         num = PhotoData[i].albumId;
        //         db[x] = {"albumId": num, "id": PhotoData[i].id, "thumbnailUrl": PhotoData[i].thumbnailUrl, "title": PhotoData[i].title};
        //         // console.log("+= " + num + " - " + x)
        //         x++;
        //         // console.log(db);
        //     }
        // }

        
        // console.log(db);

        console.log(this.state.photoDB);

        const clickedUrl = this.state.photoIndex === -1? null : this.state.photoDB[this.state.photoIndex].url;
        // const clickedTitle = this.state.photoIndex === -1? null : PhotoData[this.state.photoIndex - 1].title;

        const imageStyle={
            backgroundColor: "#fff",
            backgroundImage: 'url('+ clickedUrl +')'
        }


        //Photo Componets
        // const photoComponets = PhotoData.map(
            // let str = JSON.stringify(db);
            // console.log(str + "  + ");
            
            const photoComponets = this.state.photoDB.map(
            item => 
            <LazyPhotoComponet key={item.id} data={item}            
            albumIndex={this.state.albumIndex}
            photoIndex={this.state.photoIndex}
            handleClick={this.handleClick}
            />
            )
      return (
        <div id="Parent">
            <Suspense fallback={<div>Loading ....<img src={logo} className="App-logo" alt="logo" /></div>}>
            <div className={this.state.albumIndex ? "back" : "hide"} id="backToAlbum" onClick={this.handleClick} >BackToAlbum</div>

            {photoComponets}

            <h3 className={this.state.albumIndex ? "back" : "hide"} id="loadMore" onClick={this.handleClick} >Load more</h3>

            <div className={this.state.photoIndex === -1 ? "hide" : null} id="photo_" onClick={this.handleClick} style={imageStyle}></div>

            </Suspense>
        </div>
      );
    }
  }
  
  export default lazyContainer;
